// MARK: - RMS Norm

/// Uses ported GemmaRMSNorm (1 + weight scaling)
typealias Gemma3RMSNorm = GemmaRMSNorm

// MARK: - Utility Functions

/// Clip residual for float16 overflow protection (matching mlx-lm)
private func clipResidual(_ x: MLXArray, _ y: MLXArray) -> MLXArray {
    if x.dtype != .float16 {
        return x + y
    }
    let bound = Float16.greatestFiniteMagnitude
    let sum = (x.asType(.float32) + y.asType(.float32))
    return clip(sum, min: MLXArray(-Float(bound)), max: MLXArray(Float(bound))).asType(.float16)
}

// MARK: - Attention

class Gemma3Attention: Module {
    @ModuleInfo(key: "q_proj") var qProj: Linear
    @ModuleInfo(key: "k_proj") var kProj: Linear
    @ModuleInfo(key: "v_proj") var vProj: Linear
    @ModuleInfo(key: "o_proj") var oProj: Linear
    @ModuleInfo(key: "q_norm") var qNorm: Gemma3RMSNorm
    @ModuleInfo(key: "k_norm") var kNorm: Gemma3RMSNorm

    let numHeads: Int
    let numKVHeads: Int
    let headDim: Int
    let scale: Float
    let rope: RoPE
    let isSliding: Bool

    init(_ config: Gemma3Configuration, layerIdx: Int) {
        numHeads = config.numAttentionHeads
        numKVHeads = config.numKeyValueHeads
        headDim = config.headDim
        scale = 1.0 / sqrt(Float(headDim))

        let qDim = numHeads * headDim
        let kvDim = numKVHeads * headDim
        let attnBias = config.attentionBias

        _qProj.wrappedValue = Linear(config.hiddenSize, qDim, bias: attnBias)
        _kProj.wrappedValue = Linear(config.hiddenSize, kvDim, bias: attnBias)
        _vProj.wrappedValue = Linear(config.hiddenSize, kvDim, bias: attnBias)
        _oProj.wrappedValue = Linear(qDim, config.hiddenSize, bias: attnBias)
        _qNorm.wrappedValue = Gemma3RMSNorm(dimensions: headDim, eps: config.rmsNormEps)
        _kNorm.wrappedValue = Gemma3RMSNorm(dimensions: headDim, eps: config.rmsNormEps)
        isSliding = !config.isGlobalLayer(layerIdx)
        let ropeBase = isSliding ? config.ropeLocalBaseFreq : config.ropeTheta
        rope = RoPE(dimensions: headDim, traditional: false, base: ropeBase)
    }

    func callAsFunction(
        _ hiddenStates: MLXArray,
        mask: MLXFast.ScaledDotProductAttentionMaskMode,
        cache: inout KVCache?
    ) -> MLXArray {
        let (B, L, _) = (hiddenStates.dim(0), hiddenStates.dim(1), hiddenStates.dim(2))

        var queries = qProj(hiddenStates).reshaped([B, L, numHeads, headDim])
        queries = qNorm(queries)
        var keys = kProj(hiddenStates).reshaped([B, L, numKVHeads, headDim])
        var values = vProj(hiddenStates).reshaped([B, L, numKVHeads, headDim])
        keys = kNorm(keys)

        // Transpose for attention: [B, heads, L, headDim]
        queries = queries.transposed(0, 2, 1, 3)
        keys = keys.transposed(0, 2, 1, 3)
        values = values.transposed(0, 2, 1, 3)

        // Apply RoPE with cache offset
        let offset = cache?.offset ?? 0
        queries = rope(queries, offset: offset)
        keys = rope(keys, offset: offset)

        // Update cache
        if let c = cache {
            (keys, values) = c.update(keys: keys, values: values)
        }

        // Attention using MLXFast (handles GQA automatically)
        let output = MLXFast.scaledDotProductAttention(
            queries: queries,
            keys: keys,
            values: values,
            scale: scale,
            mask: mask
        )

        // Reshape back: [B, heads, L, headDim] -> [B, L, hidden]
        let outputReshaped = output.transposed(0, 2, 1, 3).reshaped([B, L, -1])
        return oProj(outputReshaped)
    }
}

// MARK: - MLP

class Gemma3MLP: Module {
    @ModuleInfo(key: "gate_proj") var gateProj: Linear
    @ModuleInfo(key: "up_proj") var upProj: Linear
    @ModuleInfo(key: "down_proj") var downProj: Linear

    init(_ config: Gemma3Configuration) {
        let intermediateSize = config.intermediateSize
        let mlpBias = config.mlpBias
        _gateProj.wrappedValue = Linear(config.hiddenSize, intermediateSize, bias: mlpBias)
        _upProj.wrappedValue = Linear(config.hiddenSize, intermediateSize, bias: mlpBias)
        _downProj.wrappedValue = Linear(intermediateSize, config.hiddenSize, bias: mlpBias)
    }

    func callAsFunction(_ x: MLXArray) -> MLXArray {
        downProj(geluApproximate(gateProj(x)) * upProj(x))
    }
}

// MARK: - Decoder Layer

class Gemma3DecoderLayer: Module {
    @ModuleInfo(key: "self_attn") var selfAttn: Gemma3Attention
    @ModuleInfo(key: "mlp") var mlp: Gemma3MLP
    @ModuleInfo(key: "input_layernorm") var inputLayernorm: Gemma3RMSNorm
    @ModuleInfo(key: "post_attention_layernorm") var postAttentionLayernorm: Gemma3RMSNorm
    @ModuleInfo(key: "pre_feedforward_layernorm") var preFeedforwardLayernorm: Gemma3RMSNorm
    @ModuleInfo(key: "post_feedforward_layernorm") var postFeedforwardLayernorm: Gemma3RMSNorm

    init(_ config: Gemma3Configuration, layerIdx: Int) {
        _selfAttn.wrappedValue = Gemma3Attention(config, layerIdx: layerIdx)
        _mlp.wrappedValue = Gemma3MLP(config)
        _inputLayernorm.wrappedValue = Gemma3RMSNorm(dimensions: config.hiddenSize, eps: config.rmsNormEps)
        _postAttentionLayernorm.wrappedValue = Gemma3RMSNorm(dimensions: config.hiddenSize, eps: config.rmsNormEps)
        _preFeedforwardLayernorm.wrappedValue = Gemma3RMSNorm(dimensions: config.hiddenSize, eps: config.rmsNormEps)
        _postFeedforwardLayernorm.wrappedValue = Gemma3RMSNorm(dimensions: config.hiddenSize, eps: config.rmsNormEps)
    }

    func callAsFunction(
        _ hiddenStates: MLXArray,
        mask: MLXFast.ScaledDotProductAttentionMaskMode,
        cache: inout KVCache?
    ) -> MLXArray {
        // 1. Pre-norm + Self-attention
        let normed = inputLayernorm(hiddenStates)
        let attnOut = selfAttn(normed, mask: mask, cache: &cache)
        let attnNormed = postAttentionLayernorm(attnOut)
        var h = clipResidual(hiddenStates, attnNormed)

        // 2. Pre-norm + MLP
        let mlpIn = preFeedforwardLayernorm(h)
        let mlpOut = mlp(mlpIn)
        let mlpNormed = postFeedforwardLayernorm(mlpOut)
        h = clipResidual(h, mlpNormed)
        return h
    }
}

// MARK: - Model Inner

class Gemma3ModelInner: Module {
    @ModuleInfo(key: "embed_tokens") var embedTokens: Embedding
    @ModuleInfo(key: "layers") var layers: [Gemma3DecoderLayer]
    @ModuleInfo(key: "norm") var norm: Gemma3RMSNorm

    let numLayers: Int
    let hiddenSize: Int
    let slidingWindow: Int
    let slidingWindowPattern: Int

    init(_ config: Gemma3Configuration) {
        numLayers = config.numHiddenLayers
        hiddenSize = config.hiddenSize
        slidingWindow = config.slidingWindow
        slidingWindowPattern = config.slidingWindowPattern
        _embedTokens.wrappedValue = Embedding(embeddingCount: config.vocabSize, dimensions: config.hiddenSize)
        _layers.wrappedValue = (0 ..< numLayers).map { idx in Gemma3DecoderLayer(config, layerIdx: idx) }
        _norm.wrappedValue = Gemma3RMSNorm(dimensions: config.hiddenSize, eps: config.rmsNormEps)
    }

    func callAsFunction(_ inputIds: MLXArray, cache: inout [KVCache?]) -> MLXArray {
        var hiddenStates = embedTokens(inputIds)
        let scale = MLXArray(sqrt(Float(hiddenSize)))
        hiddenStates = hiddenStates * scale.asType(hiddenStates.dtype)
        let globalLayerIdx = slidingWindowPattern - 1
        let globalCache = globalLayerIdx < cache.count ? cache[globalLayerIdx] : nil
        let globalOffset = globalCache?.offset ?? 0
        let globalMask = createAttentionMask(n: hiddenStates.dim(1), offset: globalOffset, windowSize: nil)
        let slidingMask: MLXFast.ScaledDotProductAttentionMaskMode
        if slidingWindowPattern > 1 {
            let slidingOffset = cache.first??.offset ?? 0
            slidingMask = createAttentionMask(n: hiddenStates.dim(1), offset: slidingOffset, windowSize: slidingWindow)
        } else {
            slidingMask = globalMask
        }
        for i in 0 ..< layers.count {
            let isGlobal = (i % slidingWindowPattern) == (slidingWindowPattern - 1)
            let mask = isGlobal ? globalMask : slidingMask
            hiddenStates = layers[i](hiddenStates, mask: mask, cache: &cache[i])
        }
        return norm(hiddenStates)
    }
}

