/// Apply rotary position embeddings for Gemma3n
private func gemma3nApplyRotaryPosEmb(
    _ q: MLXArray,
    _ k: MLXArray,
    cos: MLXArray,
    sin: MLXArray
) -> (MLXArray, MLXArray) {
    let qEmbed = (q * cos) + (gemma3nRotateHalf(q) * sin)
    let kEmbed = (k * cos) + (gemma3nRotateHalf(k) * sin)
    return (qEmbed, kEmbed)
}

/// Rotate half of the tensor for Gemma3n
private func gemma3nRotateHalf(_ x: MLXArray) -> MLXArray {
    let halfDim = x.dim(-1) / 2
    let x1 = x[.ellipsis, ..<halfDim]
    let x2 = x[.ellipsis, halfDim...]
    return concatenated([-x2, x1], axis: -1)
}

