class Gemma3nTransformerBlock: Module {
    @ModuleInfo(key: "self_attn") var attention: Gemma3nAttention
    let mlp: Gemma3nMLP
    @ModuleInfo(key: "input_layernorm") var inputLayerNorm: RMSNorm
    @ModuleInfo(key: "post_attention_layernorm") var postAttentionLayerNorm: RMSNorm

    init(_ config: Gemma3nConfiguration) {
        self._attention.wrappedValue = Gemma3nAttention(config)
        self.mlp = Gemma3nMLP(config)
        self._inputLayerNorm.wrappedValue = RMSNorm(
            dimensions: config.hiddenSize, eps: config.rmsNormEps ?? 1e-6)
        self._postAttentionLayerNorm.wrappedValue = RMSNorm(
            dimensions: config.hiddenSize, eps: config.rmsNormEps ?? 1e-6)
    }

    func callAsFunction(
        _ x: MLXArray,
        mask: MLXArray? = nil
    ) -> MLXArray {
        // Self-attention with residual
        let residual1 = x
        let h1 = inputLayerNorm(x)
        let attnOut = attention(h1, mask: mask)
        let h2 = residual1 + attnOut

        // MLP with residual
        let residual2 = h2
        let h3 = postAttentionLayerNorm(h2)
        let mlpOut = mlp(h3)
        return residual2 + mlpOut
    }
