    public init(_ config: Gemma3nConfiguration) {
        self._embedTokens.wrappedValue = Embedding(
            embeddingCount: config.vocabSize,
            dimensions: config.hiddenSize
        )
        self.layers = (0..<config.numHiddenLayers).map { _ in
            Gemma3nTransformerBlock(config)
        }
        self._norm.wrappedValue = RMSNorm(
            dimensions: config.hiddenSize, eps: config.rmsNormEps ?? 1e-6)
    }

    public func callAsFunction(_ inputIds: MLXArray) -> MLXArray {
        var h = embedTokens(inputIds)

        for layer in layers {
            h = layer(h, mask: nil)
        }

        return norm(h)
    }
}

